import { useEffect, useState } from "react";

// Inline CSS styles
const tableStyle = {
  fontFamily: "Arial, sans-serif",
  borderCollapse: "collapse",
  width: "100%",
  marginTop: "20px",
};

const thStyle = {
  backgroundColor: "#f2f2f2",
  padding: "10px",
  textAlign: "left",
};

const tdStyle = {
  border: "1px solid #ddd",
  padding: "8px",
  textAlign: "left",
};

const AllUsers = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    console.log(users);
  }, [users]);

  const fetchUsers = async () => {
    const response = await fetch("http://localhost:4000/users");
    const data = await response.json();
    setUsers(data.users);
    // console.log(data.users);
  };
  return (
    <div>
      <h1>All Users</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {users?.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default AllUsers;
